
public class SalutationAndNames {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        String array[][]={ {"Mr. ", "Mrs.", "Ms. "},
        		{"Pratik", "Aishwarya", "Surya", "Dishant", "Somesh", "Praveen"}};
        System.out.println(array[0][0]+" "+array[1][0]);
        System.out.println(array[0][2]+" "+array[1][1]);
        System.out.println(array[0][1]+" "+array[1][2]);
        System.out.println(array[0][0]+" "+array[1][3]);
        System.out.println(array[0][0]+" "+array[1][4]);
        System.out.println(array[0][0]+" "+array[1][5]);
        
	}

}
